import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import "./Expense.css";
const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");
  console.log(props.item[0].date.toLocaleString('en-US',{year:'numeric'})===filteredYear);
  const filterChangeHandler = (year) => {
    setFilteredYear(year);
    console.log(filteredYear);
    
  };

  return (
    <Card className="expenses">
      <ExpensesFilter
        selected={filteredYear}
        onFilterChange={filterChangeHandler}
      />
      {props.item.filter((expense) => {
        const yearBool=expense.date.toLocaleString('en-US',{year:'numeric'})===filteredYear;
        console.log("asdasdasd",yearBool)
        return yearBool? <ExpenseItem
          key={expense.id}
          titles={expense.title}
          amount={expense.amount}
          date={expense.date}
        />:"";
        })}
    </Card>
  );
};

export default Expenses;
